// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.4;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

contract PumlStake is Ownable, ReentrancyGuard {

    /* ========== STATE VARIABLES ========== */

    uint256 public rewardRate = 10;
    uint256 public lastUpdateTime;
    uint256[] public stakeData;

    mapping(address => uint256) public userLastUpdateTime;
    mapping(address => uint256) public userLastUpdateTimeNFT;
    mapping(address => uint256) public userRewardStored;
    mapping(address => uint256) public userLastReward;
    mapping(address => uint256) public userRewardPaid;

    uint256 public totalSupply;
    mapping(address => uint256) public balances;
    uint256 public totalSupplyNFT;
    mapping(address => uint256) public balancesNFT;
    mapping(uint256 => address) public stakedAssets;



    /* ========== VIEWS ========== */

    function getStakedAssets(uint256 _tokenId) public view returns (address) {
        return stakedAssets[_tokenId];
    }

    function setStakedAssets(uint256 _tokenId, address _address) public {
        stakedAssets[_tokenId] = _address;
    }

    function setTotalSupplyNFT(uint256 _amount, bool param) public {
        if (param) {
            totalSupplyNFT += _amount;
        } else {
            totalSupplyNFT -= _amount;
        }
    }

    function setBalancesNFT(address _address, uint256 _amount, bool param) public {
        if (param) {
            balancesNFT[_address] += _amount;
        } else {
            balancesNFT[_address] -= _amount;
        }
    }

    function setUserRewardStored(address _address, uint256 _amount) public {
        userRewardStored[_address] = _amount;
    }

    function setUserLastUpdateTimeNFT(address _address, uint256 _amount) public {
        userLastUpdateTimeNFT[_address] = _amount;
    }


    function lastTimeRewardApplicable() public view returns (uint256) {
        return block.timestamp;
    }

    function rewardPerUser(address account, uint256 feeward) public view returns (uint256) {
        if (totalSupply == 0) {
            return userRewardStored[account];
        } else {
            return
                userRewardStored[account] +
                ((lastTimeRewardApplicable() - userLastUpdateTime[account]) * (rewardRate / 86440) * 1e18 * balances[account]) +
                (feeward * balances[msg.sender] / totalSupply);
        }
    }

    function getRewardData(address account) public returns (uint256[] memory) {
        stakeData.push(userLastUpdateTime[account]);
        stakeData.push(userRewardStored[account]);
        stakeData.push(userLastReward[account]);
        stakeData.push(userRewardPaid[account]);
        stakeData.push(balances[account]);
        stakeData.push(balancesNFT[account]);


        return stakeData;
    }

    function rewardPerUserNFT(address account) public view returns (uint256) {
        return
            userRewardStored[account] +
            ((lastTimeRewardApplicable() - userLastUpdateTimeNFT[account]) * (rewardRate / 86440) * 1e18 * balancesNFT[account]);
    }

    
    /* ========== MUTATIVE FUNCTIONS ========== */

    function stake(uint256 amount, uint256 feeward, address staker) external payable nonReentrant updateReward(staker, feeward) {

        _stake(amount, staker);
        emit Staked(staker, amount);
    }

    function withdraw(uint256 amount, uint256 feeward) public payable nonReentrant updateReward(msg.sender, feeward) {

        _withdraw(amount);
        emit Withdrawn(msg.sender, amount);
    }

    function getReward(uint256 reward, uint256 feeward) public payable nonReentrant updateReward(msg.sender, feeward) {

        if (reward > 0) {
            userRewardStored[msg.sender] -= reward;
            userLastReward[msg.sender] = reward;
            userRewardPaid[msg.sender] += reward;

            emit RewardPaid(msg.sender, reward);
        }
    }

    function _stake(uint256 _amount, address _staker) internal {
        totalSupply += _amount;
        balances[_staker] += _amount;
    }

    function _withdraw(uint256 _amount) internal {
        totalSupply -= _amount;
        balances[msg.sender] -= _amount;
    }

    /* ========== MODIFIERS ========== */

    modifier updateReward(address account, uint256 feeward) {
        if (account != address(0)) {
            userRewardStored[account] = rewardPerUser(account, feeward);
            userLastUpdateTime[account] = lastTimeRewardApplicable();
        }
         _;
    }

    /* ========== EVENTS ========== */


    event Staked(address indexed user, uint256 amount);
    event Withdrawn(address indexed user, uint256 amount);
    event RewardPaid(address indexed user, uint256 reward);
    
}
